mapper.select:
SELECT f.id, f.contents, f.location, f.created_at , u.id AS writerUserId , u.uid AS writerUid , u.nm AS writerNickName , u.pic AS writerPic , fl.feed_id IS NOT NULL AS isLike , IFNULL(flc.likeCount, 0) AS likeCount , IFNULL(fcc.commentCount, 0) AS commentCount FROM feed f JOIN user u ON u.id = f.user_id LEFT JOIN feed_like AS fl ON fl.feed_id = f.id AND fl.user_id = #{signedUserId} LEFT JOIN ( SELECT feed_id, COUNT(feed_id) AS likeCount FROM feed_like GROUP BY feed_id ) AS flc ON flc.feed_id = f.id LEFT JOIN ( SELECT feed_id, COUNT(feed_id) AS commentCount FROM feed_comment GROUP BY feed_id ) AS fcc ON fcc.feed_id = f.id
WHERE f.user_id = #{profileUserId}
ORDER BY f.id DESC LIMIT #{startIdx}, #{size}

SELECT pic
FROM feed_pic
WHERE feed_id = #{feedId}
